	('9179BBD2:1509891971331', 'GxAvUG0BTq:1496046639003', '9179BBD2:1509891938165', 39, 1509892036, 1509892036, 'private function getFinalSQL(string $sql, array $params): string\n{\n	foreach ($params as $key => $value) {\n	    if (is_numeric($value)) {\n		$sql = str_replace($key, $value, $sql);\n	    } else {\n		$sql = str_replace($key, "\'" . $value . "\'", $sql);\n	    }\n	}\n\n	return $sql;\n}', 0, 'Get Final SQL Query', 'PlainText', '', NULL),
