	('9179BBD2:1509972046541', 'GxAvUG0BTq:1496046639003', '9179BBD2:1509972018303', 39, 1509972106, 1509972106, '\n\n    private function getOptedOutServices(): bool\n    {\n        $optOut = $this->record->emailOptOutPreferences()->toArray();\n\n        if (!array_key_exists(\'services\', $optOut)) {\n            return [];\n        }\n\n        return array_map(function($item) {\n            return new ServiceType($item);\n        }, $optOut[\'services\']);\n    }\n\n    /**\n     * @param ServiceType[] $serviceTypes\n     */\n    private function setOptedOutServices(array $serviceTypes): void\n    {\n        $optOut = $this->record->emailOptOutPreferences()->toArray();\n\n        $optOut[\'services\'] = array_filter(array_map(function($item) {\n            if ($item instanceof ServiceType) {\n                return (string)$item;\n            }\n\n            return null;\n        }, $serviceTypes));\n\n        $this->record->emailOptOutPreferences(new Json($optOut));\n    }\n    \n\n    public function serviceOptOutStatusView(): array\n    {\n        $view = [];\n        \n        $agentBranch = new AgentBranch($this->record->agentBranchId());\n        foreach ($agentBranch->services() as $service) {\n            $view[(string)$service->serviceType()] = $this->hasOptedOutOfService($service->serviceType());\n        }\n\n        return $view;\n    }\n\n\n    /*\n     * Edge Case Note\n     * --------------\n     * During agent service activation we need to be able to ignore the newly added\n     * service when performing this check (because the newly added service will already\n     * be in the agent service list by the time the command is fired).\n     */\n    public function hasOptedOutOfAllServices(ServiceType $ignoreThisService = null): bool\n    {\n        $optOutList = $this->getOptedOutServices();\n        if (empty($optOutList)) {\n            return false;\n        }\n        \n        foreach ($this->serviceOptOutStatusView() as $serviceType => $optOutSetting) {\n            if ($serviceType == (string)$ignoreThisService) {\n                continue;\n            }\n            \n            if ($optOutSetting == false) {\n                return false;\n            }\n        }\n        \n        // If we get this far, then the customer must have a "true" opt out setting\n        // for all currently available services.\n        return true;\n    }\n            \n            \n    public function hasOptedOutOfService(ServiceType $serviceType): bool\n    {\n        $optOutList = $this->getOptedOutServices();\n        if (empty($optOutList)) {\n            return false;\n        }\n        \n        return in_array($serviceType, $optOutList);\n    }\n\n    public function optInToService(ServiceType $serviceType, BasicString $sourceName): DomainEventList\n    {\n        $commandBus = new DomainEventList();\n\n        if (!$this->hasOptedOutOfService($serviceType)) {\n            return $commandBus;\n        }\n\n        $this->setOptedOutServices(array_filter($this->getOptedOutServices(), function($item) use ($serviceType) {\n           if ($item == $serviceType) {\n               return null;\n           }\n\n           return $item;\n        }));\n\n        $commandBus->append(\n            new CustomerServicePreferenceChangedEvent(\n                $this->getId(),\n                $serviceType,\n                new ServicePreferenceType(ServicePreferenceType::OPT_IN)\n            )\n        );\n\n        $commandBus->append(\n            ServiceSubscriptionEventFactory::createSubscribeEvent($this->id(), $serviceType, $sourceName)\n        );\n\n        return $commandBus;\n    }\n\n    public function optOutOfService(ServiceType $serviceType, BasicString $sourceName): DomainEventList\n    {\n        $commandBus = new DomainEventList();\n\n        if ($this->hasOptedOutOfService($serviceType)) {\n            return $commandBus;\n        }\n\n        if (!$this->agentBranch()->hasEnabledService($serviceType)) {\n            throw new \\InvalidArgumentException("Cannot opt out of service $serviceType as the agent branch does not offer it.");\n        }\n\n        $this->setOptedOutServices(array_values(array_merge(\n            $this->getOptedOutServices(),\n            [$serviceType]\n        )));\n\n        $commandBus->append(\n            new CustomerServicePreferenceChangedEvent(\n                $this->getId(),\n                $serviceType,\n                new ServicePreferenceType(ServicePreferenceType::OPT_OUT)\n            )\n        );\n\n        $commandBus->append(\n            ServiceSubscriptionEventFactory::createUnsubscribeEvent($this->id(), $serviceType, $sourceName)\n        );\n\n        return $commandBus;\n    }\n\n    public function optOutOfAllServices(BasicString $sourceName): DomainEventList\n    {\n        $commandBus = new DomainEventList();\n        $servicesOptedOut = false;\n\n        foreach ($this->serviceOptOutStatusView() as $serviceType => $isOptedOut) {\n            if ($isOptedOut) {\n                continue;\n            }\n\n            $servicesOptedOut = true;\n\n            $commandBus->append($this->optOutOfService(new ServiceType($serviceType), $sourceName));\n        }\n\n        if ($servicesOptedOut) {\n            $commandBus->append(new UnsubscribedAllServicesEvent($this->id(), $sourceName));\n        }\n\n        return $commandBus;\n    }    ', 0, 'Customer Services Opt Out', 'PlainText', '', NULL),
