	('9179BBD2:1515594255754', 'GxAvUG0BTq:1496046639003', '9179BBD2:1509891938165', 39, 1515594489, 1515598458, '<link rel="stylesheet" href="/css/select2.min.css?v={{VERSION}}_{{BUILD}}" type="text/css" />\n<script type="text/javascript" src="/js/select2.full.min.js"></script>        \n\nthis._$agentSelect.select2({\n            placeholder: \'Please select or type the name of an estate agent\',\n            dropdownParent: this._$form,\n            ajax: {\n                delay: 500,\n                transport: this.loadAgents.bind(this)\n            }\n        });\n\n    /**\n     * Called by select2 to load agents into the select list\n     * @param params an object containing the search term.\n     * @param success Function to call if loading is successful\n     * @param failure Function to call if there\'s an error during loading.\n     */\n    private loadAgents(params: any, success: any, failure: any): void\n    {\n        if (params.data.term) {\n            this._agentsList.withSearchPhrase(params.data.term);\n        }\n\n        this._$agentBranchWrapper.hide();\n        this._$agentUserWrapper.hide();\n        this._loader.render();\n\n        this._agentsList.load().then((agents: Array<Agent>) => {\n            this._agentCache = agents;\n\n            this._loader.remove();\n\n            const items: Array<any> = [];\n\n            for (let agent of agents) {\n                items.push({ \'id\': agent.id, \'text\': agent.name})\n            }\n\n            success({"results": items});\n        }, (error) => {\n            this._loader.remove();\n            this.showErrorMessage("Sorry, something has gone wrong.  " + error);\n            failure(error);\n        });\n    }\n\n        .select2 {\n            width: 370px;\n        }\n\n        .select2 .select2-selection {\n            height: 35px;\n            border-color: #d9d9d9;\n        }\n\n        .select2 .select2-selection__rendered {\n            padding-top: 2px;\n        }', 0, 'Select2', 'PlainText', '', NULL),
	('9179BBD2:1515598100106', 'GxAvUG0BTq:1496046639003', '9179BBD2:1509972018303', 39, 1515598333, 1515598333, 'MoveIt.inviteModal = function (ajaxUrl, title, supplier, continueText, editable, service) {\n    var hideAgentSelect = function (agentSelectArea) {\n        agentSelectArea.hide();\n        agentSelectArea.find(\'.select-menu-block\')\n            .empty();\n    };\n\n    var continueClicked = false;\n    var tplThankYou = $(\'#invite-new-supplier-thank-you-template\').html();\n\n    var createData = function (type, data) {\n        return {\n            type: type,\n            options: data\n        }\n    };\n\n    var showEl = function (el, type, data) {\n        // Select DOM element to append to\n        el.show();\n        el.find(\'.select-menu-block\')\n            .empty()\n            .append(twig({ref: \'agent-select-menu\'}).render(createData(type, data)));\n    };\n\n    MoveIt.modal.open({\n        templateId: \'#invite-new-supplier-template\',\n        cancelBtnText: "Cancel",\n        continueBtnText: continueText || "Send Invite",\n        data: {\n            title: title,\n            userProfile: MoveIt.config.userProfile,\n            agents: MoveIt.config.agents,\n            supplier: supplier,\n            service: service,\n            showReferralFee: MoveIt.config.serviceName.toString().toLowerCase() !== \'financial\',\n            serviceName: MoveIt.config.serviceName,\n            referralFeeList: MoveIt.config.referralFeeList,\n            editable: editable === undefined\n        },\n        callbacks: {\n            showCallback: function () {\n                if (MoveIt.config.userProfile.type == \'ADMIN\') {\n                    var agentSelect = selectList({\n                        selector: \'#estate-agent\',\n                        node: \'agents\',\n                        path: \'/ajax/agents.list\',\n                        preload: true\n                    });\n                }\n            },\n            shownCallback: function (e) {\n                var agentBranch = $(\'#agent-branch\');\n                var agentUser = $(\'#agent-user\');\n\n                var calcFee = feeCalculatorFactory(supplier, $(\'#standardModal\'), service);\n                calcFee(e);\n\n                $(\'#estate-agent\').on(\'change\', function (e) {\n                    var estateAgentId = this.value;\n                    if (estateAgentId) {\n                        $.get(\'/ajax/agents/\' + estateAgentId + \'/branches\')\n                            .done(function (response) {\n                                // Select DOM element to append to\n                                showEl(agentBranch, \'branch\', response.data);\n\n                                $(\'#estate-agent-branch\').on(\'change\', function (e) {\n                                    var branchId = this.value;\n                                    if (branchId) {\n                                        $.get(\'/ajax/agents/\' + estateAgentId + \'/branches/\' + branchId + \'/users\')\n                                            .done(function (response) {\n                                                // Select DOM element to append to\n                                                showEl(agentUser, \'user\', response.data);\n                                            });\n                                    } else {\n                                        hideAgentSelect(agentUser);\n                                    }\n                                });\n                            });\n                    } else {\n                        hideAgentSelect(agentBranch);\n                        hideAgentSelect(agentUser);\n                    }\n                });\n            },\n            continueCallback: function (e) {\n                e.preventDefault();\n                if (!continueClicked) {\n                    continueClicked = true;\n                    MoveIt.modal.showLoadingIndicator();\n\n                    var modalFormErrors = $(\'#modal-form-errors\');\n                    if (!modalFormErrors.hasClass("hide")) {\n                        modalFormErrors.addClass("hide");\n                    }\n\n                    var estateAgent = $(\'#estate-agent\').val();\n                    var estateAgentBranch = $(\'#estate-agent-branch\').val();\n                    var estateAgentUser = $(\'#estate-agent-user\').val();\n                    var companyName = $(\'#company-name\').val();\n                    var contactName = $(\'#contact-name\').val();\n                    var contactEmail = $(\'#contact-email\').val();\n                    var referralFee = supplier.referralFee !== undefined ? supplier.referralFee : \'\';\n                    modalFormErrors.addClass(\'hide\');\n\n                    var Assert = Validator.Assert;\n                    var fields = {\n                        company: companyName,\n                        name: contactName,\n                        email: contactEmail\n                    };\n                    var rules = {\n                        company: new Assert().Required(),\n                        name: new Assert().Required(),\n                        email: [new Assert().Required(), new Assert().Email()]\n                    };\n                    var labels = {\n                        company: \'company name\',\n                        name: \'contact name\'\n                    };\n\n                    if (MoveIt.config.serviceName.toString().toLowerCase() !== \'financial\' && (editable === undefined)) {\n                        fields.referralFee = referralFee;\n                        rules.referralFee = [new Assert().Required(), new Assert().Regexp(\'^\\\\d+(\\.\\\\d{1,2})?$\')]\n                        labels.referralFee = \'referral fee\';\n                    }\n\n                    if (MoveIt.config.userProfile.type == \'ADMIN\') {\n                        fields.agent = estateAgent;\n                        rules.agent = new Assert().Required();\n\n                        fields.agentBranch = estateAgentBranch;\n                        rules.agentBranch = new Assert().Required();\n                        labels.agentBranch = \'agent branch\';\n\n                        fields.agentUser = estateAgentUser;\n                        rules.agentUser = new Assert().Required();\n                        labels.agentUser = \'agent user\';\n                    }\n\n                    var messages = {\n                        Regexp: \'The ${ field } should be valid monetary amount (e.g. 150.00)\'\n                    };\n\n                    var validation = new MoveIt.Validate(messages, labels);\n                    validation.make(rules, fields);\n\n                    if (validation.check() !== true) {\n                        var messages = validation.getErrorMessages();\n                        var compiled = _.template(\'<% _.forEach(messages, function (message) {%><li><%- message[0] %></li><%}) %>\');\n                        var errorHtml = compiled({\'messages\': messages});\n                        modalFormErrors.removeClass(\'hide\');\n                        modalFormErrors.find(\'ul\').empty().html(errorHtml);\n                        continueClicked = false;\n                        MoveIt.modal.resetIndicator();\n                        return false;\n                    }\n\n                    if (MoveIt.config.userProfile.type !== \'ADMIN_USER\') {\n                        agentUserId = MoveIt.config.userProfile.id;\n                    }\n\n                    if (estateAgentUser !== undefined && estateAgentUser !== \'\') {\n                        agentUserId = estateAgentUser;\n                    }\n\n                    $.post(ajaxUrl, {\n                        supplierId: supplier.supplierId || null,\n                        agentUserId: agentUserId,\n                        name: companyName,\n                        contactName: contactName,\n                        emailAddress: contactEmail,\n                        referralFee: referralFee || null,\n                        agentBranchId: estateAgentBranch || currentBranch.id,\n                        serviceType: MoveIt.config.serviceName\n                    })\n                        .done(function (data) {\n                            continueClicked = false;\n\n                            var newData = {\n                                title: \'noChange\',\n                                emailAddress: data.data.emailAddress || data.data.supplier.emailAddress,\n                                contactName: contactName,\n                                name: data.data.name || data.data.supplier.name\n                            };\n\n                            // Reset modal settings\n                            MoveIt.modal.resetIndicator();\n                            MoveIt.modal.update(tplThankYou, newData);\n\n                            $(\'.btn-modal-popup-continue\').addClass(\'hide\');\n                            var cancelBtn = $(\'.btn-modal-popup-cancel\');\n                            cancelBtn.text(\'Ok\');\n                            cancelBtn.on(\'click\', function (e) {\n                                e.stopImmediatePropagation();\n                                window.location.reload(true);\n                            });\n                        })\n                        .fail(function (data) {\n                            var error = data.responseJSON.error;\n                            continueClicked = false;\n                            MoveIt.modal.resetIndicator();\n\n                            modalFormErrors.removeClass(\'hide\');\n                            modalFormErrors.find(\'ul\').empty().html(\'<li>\' + error + \'</li>\');\n                        });\n                }\n            },\n            cancelCallback: function (e) {\n                e.preventDefault();\n\n                continueClic